def translate_text_to_en(text):
        translator = Translator()
        translation = translator.translate(text, dest='en')
        return translation.text

def translate_text_to_en_2(text):
    translation = translate(text, 'en')
    return translation


def translate_text(text, target_language='en'):
    # Ganti 'your-api-key' dengan kunci API Anda
    api_key = 'AIzaSyClq5D1GB7EzUewH9j0ChZcGSSAS8Ls6vA'
    client = translate.Client(api_key)

    # Lakukan terjemahan
    result = client.translate(text, target_language=target_language)

